import { Component, OnInit } from '@angular/core';
import { HttpClient } from '@angular/common/http';
import { Store } from '@ngrx/store';
import { Observable } from 'rxjs';
import { User } from './store/models';
import * as fromStore from "./store";
import * as actions from "./store/actions";

@Component({
  selector: 'app-users',
  templateUrl: './users.component.html',
  styleUrls: ['./users.component.css']
})
export class UsersComponent implements OnInit {
  result!: any;
  errorMessage: any;
  users$: Observable<User[]> | undefined;
  loading$: Observable<boolean> | undefined;
  constructor(private _store$: Store,private http: HttpClient) { 
  }

  ngOnInit(): void {
    this.users$ = this._store$.select(fromStore.selectAll);
    this.loading$ = this._store$.select(fromStore.selectLoading);

    this.http.get<User>('https://jsonplaceholder.typicode.com/users').subscribe({
        next: data => {
            this.result = data as User;
        },
        error: error => {
            this.errorMessage = error.message;
            console.error('There was an error!', error);
        }
    })
  }
  save( id:string, name: string, username: string): void {
    const user = {
      id,
      name,
      username
    } as User;
    this._store$.dispatch(actions.addUser({ user: user }));
  }

  remove(user: User): void {
    this._store$.dispatch(
      actions.removeUser({ user })
    );
  }

}
